    if (!formattedErrors.length)
        return;

    // Default image.
    var imgClass = "img-unsupported"; // RESOURCE_ROOT + "icons/error_unsupported_file_type.png";

    Autodesk.Viewing.Private.AlertBox.displayErrors( container, imgClass, formattedErrors );
